import React, { useEffect, useState } from 'react';
import axios from "axios";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
  useHistory,
  useLocation
} from "react-router-dom";

function App() {
  const [isLogged, setIsLogged] = useState(false);

  // const checkIfLoggedIn = async () => {
  //
  //   return res;
  // }

  useEffect(() => {
    if (localStorage.getItem("token")) {
      const checkIfLoggedIn = async () => {
        await axios.post(process.env.REACT_APP_API_URL + "users/auth", {token: localStorage.getItem("token")})
            .then((res) => {
              setIsLogged(true);
              console.log(res);
            })
            .catch((res) => {
              setIsLogged(true);
              console.log(res)
              setTimeout(() => console.log(isLogged), 1000)
            })
      }
      checkIfLoggedIn();
    }
  }, []);



  return (
    <div className="App">
      {/*<Router>*/}
      {/*  */}
      {/*</Router>*/}
      {isLogged}

    </div>
  );
}

export default App;
