import React, { useEffect, useState } from 'react';
import axios from "axios";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
  useHistory,
  useLocation
} from "react-router-dom";
import { LoginForm } from "./components/LoginForm/LoginForm";

function App() {
  // const [isLogged, setIsLogged] = useState(false);
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [loginStatus, setLoginStatus] = useState(false);
  const [statusComponent, setStatusComponent] = useState(null);

  const checkIfLoggedIn = () => {
    if (token) {
      axios.post(process.env.REACT_APP_API_URL + "users/auth", {token})
          .then((res) => {
            console.log(res);
            if (res.data === true) {
              setLoginStatus(true);
              console.log("LOGGED IN")
            } else {
              console.log("nope")
              setLoginStatus(false);
            }
          })
          .catch((e) => {
            console.log("nope2")
            console.log(e.response)
          });
    } else {
      console.log("nope")
      setLoginStatus(false);
    }
    console.log(loginStatus)
  };

  const login = (username, password) => {
    axios.post(process.env.REACT_APP_API_URL + "users/login", {username, password})
        .then((res) => {
          localStorage.setItem("token", "Bearer" + res.data);
        })
  }

  useEffect(() => {
    checkIfLoggedIn();
  }, []);

  useEffect(() => {
    if (loginStatus) {
      const logged = (
          <div>
            Logged In!
          </div>
      );
      setStatusComponent(<Route path="/">{logged}</Route>)
    } else {
      setStatusComponent(<LoginForm onSubmit={login} />)
    }
  }, [loginStatus])


  return (
    <div className="App">
      <Router>
        <Switch>

          { statusComponent }
        </Switch>
      </Router>
    </div>
  );
}

export default App;
